import React from 'react';
import './About.css';
import theme from '../ImagesLogo/about-us.png';
import aboutimg from '../ImagesLogo/profile.jpg';
// import skillicon from '../ImagesLogo/self-development.png';
import htmlicon from '../ImagesLogo/html-5.png';
import reacticon from '../ImagesLogo/atom.png';
import jsicon from '../ImagesLogo/js.png';
import pythonicon from '../ImagesLogo/python.png';

function About() {
    return (
        <div className='about'>
            <div className="about-title">
                <h1>About Me <img src={theme} alt='' /></h1>
            </div>
            <div className="about-section">
                <div className="about-left">
                    <img src={aboutimg} alt='' />
                </div>
                <div className="about-right">
                    <div className="about-para">
                        <p>I am a passionate front-end developer with a strong enthusiasm for building engaging and acessible web application.With a solid understanding of core web technologies like HTML,CSS, and JavaScript,I have completed several projects that highlight my skills in responsive design and user interface development. </p>
                        <p>My journey into web development began with a fascination for how websites are built and function. This curiosity led me to embark on various projects, where I enjoy solving challenges while continuously learning about new technologies and best practices in front-end development.</p>
                        <p>I'm excited to collaborate with others and work as part of a team to contribute to innovative projects that push the boundaries of web design. In my spare time, I love exploring design trends, tackling coding challenges, and sharing my knowledge through a personal blog.</p>
                    </div>
                </div>
            </div>
            <div className="about-skills">
                <h3 style={{ fontSize: '35px', textAlign: 'center' }}>Skills</h3>
                <div className="about-skill">
                    <p><img src={htmlicon} alt='' style={{ marginTop: '1px', position: 'absolute', marginLeft: '-35px' }} />HTML&CSS</p><hr style={{ width: '65%' }} />
                </div>
                <div className="about-skill">
                    <p><img src={jsicon} alt='' style={{ marginTop: '1px', position: 'absolute', marginLeft: '-35px' }} />JavaScript</p><hr style={{ width: '59%' }} />
                </div>
                <div className="about-skill">
                    <p><img src={reacticon} alt='' style={{ marginTop: '1px', position: 'absolute', marginLeft: '-35px' }} />React JS</p><hr style={{ width: '67%' }} />
                </div>
                <div className="about-skill">
                    <p><img src={pythonicon} alt='' style={{ marginTop: '1px', position: 'absolute', marginLeft: '-35px' }} />Python</p><hr style={{ width: '60%' }} />
                </div>
            </div>
        </div>
    )
}

export default About
